using UnityEngine;using System.Collections;using UnityEngine.EventSystems;/**IPointerEnterHandler - OnPointerEnter - Called when a pointer enters the object•IPointerExitHandler - OnPointerExit - Called when a pointer exits the object•IPointerDownHandler - OnPointerDown - Called when a pointer is pressed on the object•IPointerUpHandler - OnPointerUp - Called when a pointer is released (called on the original the pressed object)•IPointerClickHandler - OnPointerClick - Called when a pointer is pressed and released on the same object•IInitializePotentialDragHandler - OnInitializePotentialDrag - Called when a drag target is found, can be used to initialise values•IBeginDragHandler - OnBeginDrag - Called on the drag object when dragging is about to begin•IDragHandler - OnDrag - Called on the drag object when a drag is happening•IEndDragHandler - OnEndDrag - Called on the drag object when a drag finishes•IDropHandler - OnDrop - Called on the object where a drag finishes•IScrollHandler - OnScroll - Called when a mouse wheel scrolls•IUpdateSelectedHandler - OnUpdateSelected - Called on the selected object each tick•ISelectHandler - OnSelect - Called when the object becomes the selected object•IDeselectHandler - OnDeselect - Called on the selected object becomes deselected•IMoveHandler - OnMove - Called when a move event occurs (left, right, up, down, ect)•ISubmitHandler - OnSubmit - Called when the submit button is pressed•ICancelHandler - OnCancel - Called when the cancel button is pressed ***//// <summary>/// /// </summary>
[SLua.CustomLuaClass]public class CEventReceive : MonoBehaviour//, ISelectHandler//,IPointerClickHandler, IPointerDownHandler, IPointerUpHandler, IBeginDragHandler, IDragHandler, IDropHandler, ICancelHandler{    public void OnPointerDown(BaseEventData eventData)    {        var g = EventSystem.current.currentSelectedGameObject;        UGUIEvent.onPressHandle(g, false);    }    public void OnPointerUp(BaseEventData eventData)    {        var g = EventSystem.current.currentSelectedGameObject;        UGUIEvent.onPressHandle(g, true);    }    public void OnBeginDrag(BaseEventData eventData)    {        var g = EventSystem.current.currentSelectedGameObject;        UGUIEvent.onCancelHandle(g, eventData);    }    public void OnDrag(BaseEventData eventData)    {        var g = EventSystem.current.currentSelectedGameObject;        PointerEventData ped = eventData as PointerEventData;        UGUIEvent.onDragHandle(g, ped.delta);    }    public void OnDrop(BaseEventData eventData)    {        var g = EventSystem.current.currentSelectedGameObject;        UGUIEvent.onDropHandle(g, eventData);    }     public void OnPointerClick(BaseEventData eventData)    {        PointerEventData ped = eventData as PointerEventData;        var g = EventSystem.current.currentSelectedGameObject;
        UGUIEvent.onClickHandle(g, eventData); Debug.Log("you are click "+g);    }    public void OnSelect(BaseEventData eventData)    {        var g = EventSystem.current.currentSelectedGameObject;        UGUIEvent.onCancelHandle(g, eventData);    }    public void OnCancel(BaseEventData eventData)    {        var g=EventSystem.current.currentSelectedGameObject;        UGUIEvent.onCancelHandle(g, eventData);    }}